import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AttendanceDetails = () => {
  const { id } = useParams();  // Using useParams to get ID from URL
  const [attendance, setAttendance] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        // Fetching the attendance details using the ID from the URL
        const response = await axios.get(`http://localhost:5003/api/attendance/details/${id}`);
        setAttendance(response.data);  // Setting the data in the state
      } catch (error) {
        console.error('Failed to fetch attendance details:', error);
        setError(error);
      }
    };

    if (id) {
      fetchAttendance();  // Fetch the attendance details if the ID is available
    }
  }, [id]);  // Re-run the effect if the ID changes

  // Display error message if an error occurs
  if (error) return <div>Error: {JSON.stringify(error)}</div>;

  // Render the attendance details if data is available
  if (attendance) {
    return (
      <div style={{ padding: '2rem' }}>
  <h2 style={{ fontSize: '1.8rem', marginBottom: '1rem' }}>Meeting Notes</h2>

  <button
    onClick={handleCreateMinuteNote}
    style={{
      backgroundColor: '#add8f7',
      color: 'black',
      border: 'none',
      borderRadius: '25px',
      padding: '10px 20px',
      fontSize: '1rem',
      cursor: 'pointer',
      marginBottom: '1.5rem'
    }}
  >
    Create New Minute Note
  </button>

  <div style={{
    backgroundColor: '#d3eafd',
    padding: '1rem 1.5rem',
    borderRadius: '15px',
    border: '1px solid #333',
    width: 'fit-content',
    fontSize: '1.1rem'
  }}>
    <p><strong>Title:</strong> <em>Title</em></p>
    <p><strong>Content:</strong> <em>Content</em></p>
    <p><strong>Created:</strong> <em>Created</em></p>

    <div style={{ marginTop: '1rem', display: 'flex', gap: '10px' }}>
      <button
        style={{
          backgroundColor: '#d3d3d3',
          border: 'none',
          borderRadius: '15px',
          padding: '6px 15px',
          cursor: 'pointer'
        }}
      >
        Edit
      </button>

      <button
        style={{
          backgroundColor: '#f66',
          border: 'none',
          borderRadius: '15px',
          padding: '6px 15px',
          color: 'white',
          cursor: 'pointer'
        }}
      >
        Delete
      </button>
    </div>
  </div>
</div>

    );
  }

  return <div>Loading...</div>;
};

export default AttendanceDetails;
