// src/pages/attendance/AttendanceForm.jsx
import React, { useState } from 'react';
import axios from 'axios';

const departments = ['HR', 'IT', 'Finance', 'Marketing'];

const AttendanceForm = () => {
  const [meetingDate, setMeetingDate] = useState(new Date().toISOString().split('T')[0]);
  const [attendees, setAttendees] = useState([{ name: '', department: '' }]);

  const handleChange = (index, field, value) => {
    const updated = [...attendees];
    updated[index][field] = value;
    setAttendees(updated);
  };

  const addRow = () => {
    setAttendees([...attendees, { name: '', department: '' }]);
  };

  const handleSubmit = async () => {
    try {
      await axios.post('/api/attendance/create', {
        meeting_date: meetingDate,
        attendees,
        created_by: 'your-email@gmail.com' // optional
      });
      alert('Attendance saved!');
      setAttendees([{ name: '', department: '' }]);
    } catch (err) {
      console.error(err);
      alert('Error saving attendance.');
    }
  };

  return (
    <div>
      <h2>Create Attendance</h2>
      <label>
        Date:
        <input type="date" value={meetingDate} onChange={e => setMeetingDate(e.target.value)} />
      </label>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Department</th>
          </tr>
        </thead>
        <tbody>
          {attendees.map((person, idx) => (
            <tr key={idx}>
              <td>
                <input
                  type="text"
                  value={person.name}
                  onChange={e => handleChange(idx, 'name', e.target.value)}
                />
              </td>
              <td>
                <select
                  value={person.department}
                  onChange={e => handleChange(idx, 'department', e.target.value)}
                >
                  <option value="">Select</option>
                  {departments.map(dep => (
                    <option key={dep} value={dep}>{dep}</option>
                  ))}
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={addRow}>Add Attendee</button>
      <button onClick={handleSubmit}>Save Attendance</button>
    </div>
  );
};

export default AttendanceForm;
