import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getNotes } from '../../services/notesService';

const NotesListPage = () => {
  const [notes, setNotes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getNotes()
      .then((res) => {
        setNotes(res.data);
      })
      .catch((err) => console.error('Error fetching notes:', err));
  }, []);

  const handleCreateNote = () => {
    navigate('/create_notes'); 
  };

  const handleNoteClick = (id) => {
    navigate(`/note/${id}`); // ðŸ‘ˆ Navigate to the edit/delete page for that note
  };

  return (
    <div>
      <h1>All Notes</h1>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {notes.map((note) => (
          <li
            key={note._id}
            onClick={() => handleNoteClick(note._id)}
            style={{
              border: '1px solid #ccc',
              padding: '10px',
              marginBottom: '10px',
              cursor: 'pointer',
              borderRadius: '8px',
              background: '#f9f9f9',
            }}
          >
            <h3>{note.title}</h3>
            <p>{note.content.length > 100 ? note.content.slice(0, 100) + '...' : note.content}</p>
          </li>
        ))}
      </ul>
      <button onClick={handleCreateNote}>Create New Note</button>
    </div>
  );
};

export default NotesListPage;
