import React, { useEffect, useState } from 'react';
import { getNotes } from '../services/notesService';
import { useNavigate } from 'react-router-dom';

const MeetingScreen = () => {
  const [meetingNotes, setMeetingNotes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMeetingNotes = async () => {
      try {
        const response = await getNotes();
        
        // Log the response data to check the structure and `isMinute` field
        console.log('Fetched notes:', response.data);

        // Filter notes to only include meeting notes (isMinute === true)
        const meetingNotes = response.data.filter(note => note.isMinute === true);
        
        // Log the filtered meeting notes
        console.log('Filtered meeting notes:', meetingNotes);

        // Set the state with the filtered meeting notes
        setMeetingNotes(meetingNotes);
      } catch (err) {
        console.error('Error fetching notes:', err);
      }
    };
    
    fetchMeetingNotes();
  }, []);  // Empty dependency array to run only once when the component mounts

  const handleNoteClick = (noteId) => {
    navigate(`/note/${noteId}`); // Navigate to the note detail page
  };

  return (
    <div>
      <h1>Meeting Notes</h1>
      {meetingNotes.length === 0 ? (
        <p>No meeting notes found.</p>
      ) : (
        <ul>
          {meetingNotes.map(note => (
            <li key={note._id} onClick={() => handleNoteClick(note._id)}>
              {note.title}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MeetingScreen;
