import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AttendanceDetails = () => {
  const { id } = useParams(); // This should match your route definition
  const [attendance, setAttendance] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        const res = await axios.get(`http://localhost:5003/api/attendance/details/${id}`);
        setAttendance(res.data);
      } catch (err) {
        console.error("Failed to fetch attendance details:", err);
        setError(err.response?.data || "Failed to fetch data");
      }
    };

    if (id) {
      fetchAttendance();
    }
  }, [id]);

  if (error) return <div>Error: {JSON.stringify(error)}</div>;
  if (!attendance) return <div>Loading...</div>;

  return (
    <div>
      <h2>Attendance Details</h2>
      <p><strong>ID:</strong> {attendance._id}</p>
      <p><strong>Date:</strong> {new Date(attendance.meeting_date).toLocaleDateString()}</p>
      <p><strong>Attendees:</strong> {attendance.attendees.join(', ')}</p>
    </div>
  );
};

export default AttendanceDetails;
