import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { getNotes, deleteNote, getTicketStatus } from '../../services/notesService';

const MeetingScreen = () => {
  const [meetingNotes, setMeetingNotes] = useState({
    approved: [],
    forApproval: [],
    declined: []
  });
  const navigate = useNavigate();
  const location = useLocation();

  // Fetch meeting notes along with ticket statuses
  const fetchMeetingNotes = async () => {
    try {
      const response = await getNotes();  // Fetch notes
      const allNotes = response.data;

      // Fetch ticket statuses for all notes
      const notesWithStatus = await Promise.all(
        allNotes.map(async (note) => {
          try {
            const res = await getTicketStatus(note._id);  // Fetch status for each note
            const ticketStatus = res.data.ticket[0]?.status || 'Unavailable';  // Using optional chaining in case there's no status

            return {
              ...note,
              ticketStatus,  // Save the status to note
            };
          } catch (err) {
            return {
              ...note,
              ticketStatus: 'Unavailable',  // If there's an error, mark as 'Unavailable'
            };
          }
        })
      );

      // Categorize notes by ticket status
      const approvedNotes = notesWithStatus.filter(note => note.ticketStatus === 'Approved');
      const forApprovalNotes = notesWithStatus.filter(note => note.ticketStatus === 'For Approval');
      const declinedNotes = notesWithStatus.filter(note => note.ticketStatus === 'Declined');

      setMeetingNotes({ approved: approvedNotes, forApproval: forApprovalNotes, declined: declinedNotes });

    } catch (err) {
      console.error('Error fetching notes:', err);
    }
  };

  useEffect(() => {
    fetchMeetingNotes();  // Fetch notes whenever the location changes
  }, [location]);

  const handleNoteClick = (noteId) => {
    navigate(`/minute_note_detail/${noteId}`);  // Navigate to note detail page
  };

  const formatDate = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundColor: '#141414',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      overflowY: 'auto',
      padding: '2rem',
      boxSizing: 'border-box'
    }}>
      <br></br><br></br>
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backdropFilter: 'blur(8px)',
        WebkitBackdropFilter: 'blur(8px)',
        zIndex: 0
      }} />

      <div style={{
        position: 'relative',
        maxWidth: '1200px',
        margin: '0 auto',
        zIndex: 1
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '2rem',
          flexWrap: 'wrap',
          gap: '1rem'
        }}>
          <h1 className="wave-text" style={{
            margin: 0,
            fontWeight: '600',
            textShadow: '0 2px 4px rgba(0,0,0,0.3)',
            display: 'flex', 
            gap: '0.2rem', 
            color: 'white',
          }}>
            {'Meeting Notes'.split('').map((char, index) => (
              <span key={index} style={{ animationDelay: `${index * 0.2}s` }}>
                {char === ' ' ? '\u00A0' : char} {/* Handle spaces */}
              </span>
            ))}
          </h1>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <button
              onClick={() => navigate('/minute_create')}
              style={{
                backgroundColor: '#4973ff',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '0.75rem 1.5rem',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
                ':hover': {
                  backgroundColor: '#2563eb',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 8px rgba(0,0,0,0.3)'
                }
              }}
            >
              Create New Minute
            </button>

            <button
              onClick={() => navigate('/login')}
              style={{
                backgroundColor: 'rgba(51, 65, 85, 0.7)',
                color: 'rgba(248, 250, 252, 0.9)',
                border: '1px solid rgba(255,255,255,0.2)',
                borderRadius: '8px',
                padding: '0.75rem 1.5rem',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                ':hover': {
                  backgroundColor: 'rgba(51, 65, 85, 0.9)',
                  transform: 'translateY(-2px)'
                }
              }}
            >
              Logout
            </button>
          </div>
        </div>

        {['approved', 'forApproval', 'declined'].map((status) => (
          <div key={status}>
            <h2 style={{ color: 'white', marginBottom: '1rem' }}>
              {status.charAt(0).toUpperCase() + status.slice(1)} Notes
            </h2>
            {meetingNotes[status]?.length === 0 ? (
              <div style={{
                backgroundColor: 'rgba(51, 65, 85, 0.5)',
                borderRadius: '12px',
                padding: '2rem',
                textAlign: 'center',
                color: 'rgba(248, 250, 252, 0.7)',
                border: '1px dashed rgba(255,255,255,0.1)'
              }}>
                <p style={{ margin: 0, fontSize: '1.1rem' }}>
                  No {status} meeting minutes found
                </p>
              </div>
            ) : (
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
                gap: '1.5rem',
                marginTop: '1rem'
              }}>
                {meetingNotes[status].map(note => (
                  <div
                    key={note._id}
                    onClick={() => handleNoteClick(note._id)}
                    className="note-card"
                  >
                    <h3 style={{
                      color: '#f8fafc',
                      marginTop: 0,
                      marginBottom: '1rem',
                      fontSize: '1.25rem',
                      fontWeight: '600',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}>
                      {note.title}
                    </h3>
                    <div style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                      color: 'rgba(248, 250, 252, 0.6)',
                      fontSize: '0.85rem',
                      marginTop: '1.5rem'
                    }}>
                      <span>{formatDate(note.createdAt)}</span>
                      <span style={{
                        backgroundColor: 'rgba(59, 130, 246, 0.2)',
                        color: '#93c5fd',
                        padding: '0.25rem 0.5rem',
                        borderRadius: '4px',
                        fontSize: '0.75rem'
                      }}>
                        {note.ticketStatus}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MeetingScreen;
