import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { postAttendance } from '../../api';

const AttendanceForm = () => {
  const [meetingDate, setMeetingDate] = useState('');
  const [attendees, setAttendees] = useState([{ name: '', department: '' }]);
  const navigate = useNavigate();

  const departments = ['HR', 'IT', 'Finance', 'Admin', 'Marketing'];

  const handleChange = (index, field, value) => {
    const updated = [...attendees];
    updated[index][field] = value;
    setAttendees(updated);
  };

  const addAttendee = () => {
    setAttendees([...attendees, { name: '', department: '' }]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    postAttendance({ meeting_date: meetingDate, attendees })
      .then(() => navigate('/attendance'))
      .catch(err => console.error('Submit error:', err));
  };

  return (
    <div>
      <h2>Add Attendance</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Meeting Date:
          <input type="date" value={meetingDate} onChange={e => setMeetingDate(e.target.value)} required />
        </label>

        {attendees.map((att, index) => (
          <div key={index}>
            <input
              type="text"
              placeholder="Name"
              value={att.name}
              onChange={(e) => handleChange(index, 'name', e.target.value)}
              required
            />
            <select
              value={att.department}
              onChange={(e) => handleChange(index, 'department', e.target.value)}
              required
            >
              <option value="">Select Dept</option>
              {departments.map(dep => (
                <option key={dep} value={dep}>{dep}</option>
              ))}
            </select>
          </div>
        ))}

        <button type="button" onClick={addAttendee}>Add More</button>
        <br />
        <button type="submit">Save Attendance</button>
      </form>
    </div>
  );
};

export default AttendanceForm;
