import React, { useState } from 'react';
import axios from 'axios';

const AttendanceForm = () => {
  const [meetingDate, setMeetingDate] = useState('');
  const [attendees, setAttendees] = useState('');
  const [createdBy, setCreatedBy] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the attendance data
    const attendanceData = {
      meeting_date: meetingDate,  // Ensure this is a valid date string (ISO format)
      attendees: attendees.split(',').map((att) => att.trim()),  // Split string into an array
      created_by: createdBy,
    };

    console.log("Submitting attendance data:", attendanceData); // Log the data being sent

    try {
      const response = await axios.post('http://localhost:5003/api/attendance/create', attendanceData);
      console.log("Attendance created:", response.data);
      alert('Attendance created successfully!');
    } catch (error) {
      console.error('Error submitting attendance:', error.response ? error.response.data : error.message);
      setError('Failed to create attendance: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  return (
    <div>
      <h2>Create Attendance</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Meeting Date:</label>
          <input
            type="date"
            value={meetingDate}
            onChange={(e) => setMeetingDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Attendees (comma separated):</label>
          <input
            type="text"
            value={attendees}
            onChange={(e) => setAttendees(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Created By:</label>
          <input
            type="text"
            value={createdBy}
            onChange={(e) => setCreatedBy(e.target.value)}
            required
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AttendanceForm;
