import axios from 'axios';

const API_BASE_URL = 'http://localhost:5003/api/notes';

// Fetching notes with console log for debugging
export const getNotes = async () => {
  try {
    const response = await axios.get(API_BASE_URL);
    console.log('API Response:', response.data);  // Debugging: Log the full response to see what it contains
    return response.data;
  } catch (err) {
    console.error('Error fetching notes:', err);
    throw err;  // Ensure to throw errors for proper error handling
  }
};

export const createNote = (data) => axios.post(API_BASE_URL, data);
export const updateNote = (id, data) => axios.put(`${API_BASE_URL}/${id}`, data);
export const submitNoteForApproval = (id) => axios.patch(`${API_BASE_URL}/${id}/submit`);
