import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNoteById, updateNote } from '../../services/notesService';
import { getAttendanceById } from '../../services/attendanceService';

const MinuteNoteDetail = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);
  const [attendees, setAttendees] = useState([]);
  const [attendeeIdInput, setAttendeeIdInput] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNote = async () => {
      try {
        const response = await getNoteById(id);
        setNote(response.data);
        // If you already store attendees in the note, initialize them
        if (response.data.attendees) {
          setAttendees(response.data.attendees);
        }
      } catch (err) {
        console.error('Error fetching note:', err);
      }
    };

    fetchNote();
  }, [id]);

  const handleEdit = () => {
    navigate(`/minute_note_edit/${id}`);
  };

  const handleSubmitForApproval = async () => {
    try {
      await updateNote(id, { submitted: true });
      alert('Note submitted for approval!');
    } catch (err) {
      console.error('Error submitting note:', err);
    }
  };

  const handleAddAttendee = async () => {
    try {
      const response = await getAttendanceById(attendeeIdInput);
      const attendee = response.data;

      setAttendees((prev) => [...prev, attendee]);
      setAttendeeIdInput('');

      // Optionally save this to the note's DB record:
      // await updateNote(id, { attendees: [...attendees, attendee] });
    } catch (err) {
      alert('Attendee not found with that ID');
      console.error('Error fetching attendee:', err);
    }
  };

  if (!note) return <div>Loading...</div>;

  return (
    <div>
      <h1>{note.title}</h1>
      <p>{note.content}</p>

      <h3>Attendees</h3>
      <ul>
        {attendees.length === 0 ? (
          <li>No attendees yet.</li>
        ) : (
          attendees.map((att, index) => (
            <li key={att._id || index}>{att.name || `Unnamed (${att._id})`}</li>
          ))
        )}
      </ul>

      <input
        type="text"
        value={attendeeIdInput}
        onChange={(e) => setAttendeeIdInput(e.target.value)}
        placeholder="Enter Attendee ID"
      />
      <button onClick={handleAddAttendee}>Add Attendee</button>

      <div style={{ marginTop: '20px' }}>
        <button onClick={handleSubmitForApproval}>Send for Approval</button>
        <button onClick={handleEdit}>Edit</button>
      </div>
    </div>
  );
};

export default MinuteNoteDetail;
