import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import NotesPage from './pages/notes_listpage';
import CreateNotePage from './pages/create_notes';
import GoogleMeetButton from './components/GoogleMeetButton';
import { getAttendance } from './services/api';

const AttendancePage = () => {
  const [attendanceList, setAttendanceList] = useState([]);

  useEffect(() => {
    getAttendance()
      .then((res) => setAttendanceList(res.data))
      .catch((err) => console.error('Error fetching attendance:', err));
  }, []);

  return (
    <div>
      <h1>Attendance Records</h1>
      <ul>
        {attendanceList.map((entry, index) => (
          <li key={index}>{entry.name} - {entry.status}</li>
        ))}
      </ul>
    </div>
  );
};

function App() {
  return (
    <Router>
      <div style={{ display: 'flex' }}>
        {/* Sidebar */}
        <div style={{
          width: '200px',
          padding: '10px',
          borderRight: '1px solid #ccc',
          height: '100vh'
        }}>
          <Link to="/"><button>Notes</button></Link>
          <Link to="/create_notes"><button>Create Note</button></Link>
          <Link to="/google_meet"><button>Google Meet</button></Link>
          <Link to="/attendance"><button>Attendance</button></Link>
        </div>

        {/* Main Content */}
        <div style={{ flex: 1, padding: '20px' }}>
          <Routes>
            <Route path="/" element={<NotesPage />} />
            <Route path="/create_notes" element={<CreateNotePage />} />
            <Route path="/google_meet" element={<GoogleMeetButton />} />
            <Route path="/attendance" element={<AttendancePage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
