import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNoteById, updateNote } from '../../services/notesService';
import { getAllUsers } from '../../services/userService'; // Assuming a function to fetch all users

const MinuteNoteDetail = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedAttendees, setSelectedAttendees] = useState([]);
  const navigate = useNavigate();

  // Fetch the note and all users on initial load
  useEffect(() => {
    const fetchNoteAndUsers = async () => {
      try {
        // Get the note data
        const noteResponse = await getNoteById(id);
        setNote(noteResponse.data);
        console.log(noteResponse.data);

        // Get all users
        const usersResponse = await getAllUsers();
        setUsers(usersResponse.data.users);
    

        // If attendees exist in the note, pre-check the ones that were selected
        if (noteResponse.data.attendees) {
          setSelectedAttendees(noteResponse.data.attendees);
        }
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchNoteAndUsers();
  }, [id]);

  // Handle the save/update action
  const handleSave = async () => {
    try {
      // Update the note with the selected attendees
      await updateNote(id, { attendees: selectedAttendees });
      alert('Note updated successfully!');
    } catch (err) {
      console.error('Error saving note:', err);
    }
  };

  // Handle checkbox change
  const handleAttendeeChange = (userId) => {
    setSelectedAttendees((prevSelected) => {
      if (prevSelected.includes(userId)) {
        return prevSelected.filter((id) => id !== userId); // Deselect if already selected
      }
      return [...prevSelected, userId]; // Add userId if not selected
    });
  };

  // Handle "Mark All" functionality
  const handleMarkAll = () => {
    if (selectedAttendees.length === users.length) {
      setSelectedAttendees([]); // Unselect all if already selected
    } else {
      setSelectedAttendees(users.map((user) => user.id)); // Select all users
    }
  };

  if (!note || !users.length) return <div>Loading...</div>;

  return (
    <div style={{ padding: '20px', fontFamily: 'Segoe UI, sans-serif' }}>
      <h1 style={{ fontStyle: 'italic', fontWeight: 'normal' }}>{note.title}</h1>

      <p style={{ backgroundColor: '#e0ecff', padding: '20px', borderRadius: '10px', fontStyle: 'italic', minWidth: '400px', minHeight: '100px', width: 'fit-content', maxWidth: '600px', marginBottom: '20px' }}>
        {note.content}
      </p>

      <h3 style={{ marginTop: '20px' }}>Attendees:</h3>
      <button onClick={handleMarkAll} style={{ marginBottom: '10px', padding: '6px 12px', backgroundColor: '#b4d8f7', border: 'none', borderRadius: '10px' }}>
        {selectedAttendees.length === users.length ? 'Unmark All' : 'Mark All'}
      </button>
      <ul style={{ listStyleType: 'none', paddingLeft: '0' }}>
        {users.map((user) => (
          <li key={user.id} style={{ marginBottom: '10px' }}>
            <label style={{ fontStyle: 'italic' }}>
              <input
                type="checkbox"
                checked={selectedAttendees.includes(user.id)}
                onChange={() => handleAttendeeChange(user.id)}
                style={{ marginRight: '10px' }}
              />
              {user.firstName} {user.lastName}
            </label>
          </li>
        ))}
      </ul>

      <div style={{ marginTop: '20px', display: 'flex', gap: '10px' }}>
        <button
          onClick={handleSave}
          style={{
            backgroundColor: '#d9d9d9',
            border: 'none',
            borderRadius: '10px',
            padding: '6px 12px',
            cursor: 'pointer',
          }}
        >
          Save
        </button>
      </div>
    </div>
  );
};

export default MinuteNoteDetail;
