import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

function MinuteNoteEdit() {
  const { id } = useParams(); // To get the note ID from the URL
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const navigate = useNavigate();

  // Fetch the current note data
  useEffect(() => {
    const fetchNote = async () => {
      try {
        const response = await axios.get(`/api/notes/${id}`);
        setTitle(response.data.title);
        setContent(response.data.content);
      } catch (error) {
        console.error('Error fetching note:', error);
      }
    };

    fetchNote();
  }, [id]);

  const handleSubmit = async () => {
    try {
      await axios.put(`/api/notes/${id}`, { title, content, isMinute: true });
      console.log('Note updated!');
      navigate('/meeting_screen'); // Go back after update
    } catch (error) {
      console.error('Update failed:', error);
    }
  };

  return (
    <div>
      <h1>Edit Minute Note</h1>
      <form onSubmit={(e) => { e.preventDefault(); handleSubmit(); }}>
        <div>
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Content</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
          />
        </div>
        <button type="submit">Update Note</button>
      </form>
    </div>
  );
}

export default MinuteNoteEdit;
