import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AttendanceDetails = () => {
  const { id } = useParams();
  const [attendance, setAttendance] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        const response = await axios.get(`http://localhost:5003/api/attendance/details/${id}`);
        setAttendance(response.data);
      } catch (err) {
        console.error('Failed to fetch attendance details:', err);
        setError('Could not load attendance details.');
      }
    };
    fetchAttendance();
  }, [id]);

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  if (!attendance) {
    return <p>Loading attendance details...</p>;
  }

  return (
    <div>
      <h2>Attendance Details</h2>
      <p><strong>Meeting Date:</strong> {new Date(attendance.meeting_date).toLocaleDateString()}</p>
      <p><strong>Created By:</strong> {attendance.created_by}</p>
      <p><strong>Attendees:</strong></p>
      <ul>
        {(attendance.attendees || []).map((attendee, index) => (
          <li key={index}>{attendee}</li>
        ))}
      </ul>
    </div>
  );
};

export default AttendanceDetails;
