import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function AttendanceDetails() {
  const { id } = useParams();
  const [attendance, setAttendance] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        const response = await axios.get(`http://localhost:5003/api/attendance/details/${id}`);
        setAttendance(response.data);
      } catch (err) {
        console.error("Failed to fetch attendance details:", err);
        setError("Could not load attendance details.");
      } finally {
        setLoading(false);
      }
    };

    fetchAttendance();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!attendance || !Array.isArray(attendance.students)) return <p>No attendance data found.</p>;

  return (
    <div>
      <h2>Attendance Details</h2>
      <p><strong>Date:</strong> {attendance.date}</p>
      <p><strong>Title:</strong> {attendance.title}</p>

      <h3>Students</h3>
      <ul>
        {attendance.students.map((student, index) => (
          <li key={index}>
            {student.name} - {student.status}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AttendanceDetails;
