import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getNotes, updateNote, submitNoteForApproval } from '../services/notesService';

const MinuteNoteDetail = () => {
  const [note, setNote] = useState(null);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const { id } = useParams();  // Get the note ID from the URL
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the note details based on ID
    const fetchNote = async () => {
      try {
        const response = await getNotes();
        const noteData = response.data.find(note => note._id === id);
        setNote(noteData);
        setTitle(noteData.title);
        setContent(noteData.content);
      } catch (err) {
        console.error('Error fetching note:', err);
      }
    };

    fetchNote();
  }, [id]);

  const handleSave = async () => {
    try {
      await updateNote(id, { title, content });
      alert('Note updated successfully!');
      navigate('/');
    } catch (err) {
      console.error('Error updating note:', err);
    }
  };

  const handleSubmit = async () => {
    try {
      await submitNoteForApproval(id);
      alert('Note submitted for approval!');
      navigate('/');
    } catch (err) {
      console.error('Error submitting note:', err);
    }
  };

  return (
    <div>
      <h1>Edit Note</h1>
      {note ? (
        <div>
          <form>
            <div>
              <label>Title:</label>
              <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <div>
              <label>Content:</label>
              <textarea
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
            </div>
          </form>
          <button onClick={handleSave}>Save Note</button>
          <button onClick={handleSubmit}>Submit for Approval</button>
        </div>
      ) : (
        <p>Loading note...</p>
      )}
    </div>
  );
};

export default MinuteNoteDetail;
