// src/pages/meetings/minute_note_edit.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNoteById, updateNote } from '../../services/notesService';

function MinuteNoteEdit() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [note, setNote] = useState({ title: '', content: '' });

  useEffect(() => {
    const fetchNote = async () => {
      try {
        const response = await getNoteById(id);
        setNote(response.data);
      } catch (error) {
        console.error('Failed to fetch note:', error);
      }
    };

    fetchNote();
  }, [id]);

  const handleChange = (e) => {
    setNote({ ...note, content: e.target.value });
  };

  const handleSave = async () => {
    try {
      await updateNote(id, { content: note.content });
      navigate('/meeting_screen');
    } catch (error) {
      console.error('Failed to save note:', error);
    }
  };

  return (
    <div>
      <h2>Edit Minute Note</h2>
      <label>Title:</label>
      <input type="text" value={note.title} readOnly />
      <br />
      <label>Content:</label>
      <textarea value={note.content} onChange={handleChange} />
      <br />
      <button onClick={handleSave}>Save</button>
    </div>
  );
}

export default MinuteNoteEdit;
