import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AttendanceDetails = () => {
  const { id } = useParams(); // This should match your route definition
  const [attendance, setAttendance] = useState(null);
  const [error, setError] = useState(null);
console.log(AttendanceDetails);
  useEffect(() => {
    const fetchAttendance = async () => {
        try {
          // Get the current URL
          const currentUrl = window.location.href;
          
          // Extract the last part of the URL after the last "/"
          const attendanceId = currentUrl.split('/').pop(); // This gives you the last segment of the URL
          
          console.log(attendanceId); // Check if you got the correct attendanceId
          
          // Make the API request using the extracted attendanceId
          const response = await axios.get(`http://localhost:5003/api/attendance/details/${attendanceId}`);
          console.log(response.data); // Do something with the response
        } catch (error) {
          console.error('Failed to fetch attendance details:', error);
        }
      };
      

    if (id) {
      fetchAttendance();
    }
  }, [id]);

  if (error) return <div>Error: {JSON.stringify(error)}</div>;
  if (!attendance) return <div>Loading...</div>;

  return (
    <div>
      <h2>Attendance Details</h2>
      <p><strong>ID:</strong> {attendance._id}</p>
      <p><strong>Date:</strong> {new Date(attendance.meeting_date).toLocaleDateString()}</p>
      <p><strong>Attendees:</strong> {attendance.attendees.join(', ')}</p>
    </div>
  );
};

export default AttendanceDetails;
