import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createNote } from '../services/notesService';

const CreateNotePage = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [category, setCategory] = useState('notes');  // Add category (Notes or Meeting)
  const navigate = useNavigate();

  const handleSubmit = async (e, category) => {
    e.preventDefault();
    if (!title || !content) {
      console.error("Title and content cannot be empty.");
      return;
    }
    try {
      // Save note with category (either 'notes' or 'meeting')
      const response = await createNote({ title, content, category });
      console.log('Response from API:', response.data);

      if (category === 'notes') {
        navigate('/'); // Navigate back to the Notes screen
      } else {
        navigate('/meeting'); // Navigate to the Meeting screen
      }
    } catch (err) {
      console.error('Error creating note:', err);
    }
  };

  return (
    <div>
      <h1>Create Note</h1>
      <form>
        <div>
          <label>Title:</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Content:</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
          />
        </div>
        <button type="button" onClick={(e) => handleSubmit(e, 'notes')}>Save to Notes</button>
        <button type="button" onClick={(e) => handleSubmit(e, 'meeting')}>Save to Meeting</button>
      </form>
    </div>
  );
};

export default CreateNotePage;
