import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function MinuteNoteEdit() {
  const { id } = useParams(); // Get the note ID from the URL
  const navigate = useNavigate();

  const [note, setNote] = useState({ title: '', content: '' });

  // Fetch the note to edit
  useEffect(() => {
    const fetchNote = async () => {
      try {
        const response = await axios.get(`/api/notes/${id}`);
        setNote(response.data);
      } catch (error) {
        console.error('Failed to fetch note:', error);
      }
    };

    fetchNote();
  }, [id]);

  // ðŸ‘‡ Define the missing function here
  const handleSave = async () => {
    try {
      await axios.put(`/api/notes/${id}`, note);
      navigate('/meeting_screen'); // Go back to the meeting screen after saving
    } catch (error) {
      console.error('Failed to save note:', error);
    }
  };

  const handleChange = (e) => {
    setNote({ ...note, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <h2>Edit Minute Note</h2>
      <input
        type="text"
        name="title"
        value={note.title}
        onChange={handleChange}
        placeholder="Title"
      />
      <br />
      <textarea
        name="content"
        value={note.content}
        onChange={handleChange}
        placeholder="Content"
      />
      <br />
      <button onClick={handleSave}>Save</button>
    </div>
  );
}

export default MinuteNoteEdit;
