import axios from 'axios';

const API_BASE_URL = 'http://localhost:5003/api/notes';

export const getNotes = () => axios.get(API_BASE_URL);
export const getNoteById = (id) => axios.get(`${API_BASE_URL}/${id}`);
export const createNote = (data) => axios.post(API_BASE_URL, data);
export const updateNote = (id, data) => axios.put(`${API_BASE_URL}/${id}`, data);  // Update note
export const submitNoteForApproval = (id) => axios.patch(`${API_BASE_URL}/${id}/submit`);  // Submit for approval
