import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createNote } from '../../services/notesService';
import axios from 'axios';

const MinuteCreate = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const navigate = useNavigate();
const handleSubmit = async (e) => {
  e.preventDefault();

  const noteData = {
    title: title.trim(),
    content: content.trim(),
    isMinute: true,
    attendees: null,
  };

  try {
    const createdNote = await createNote(noteData);

    if (!createdNote || !createdNote._id) {
      throw new Error('Note ID not returned from server');
    }

    // Now send to external API
    await axios.post('https://express-auro.onrender.com/api/ticket/create/mnas', {
      reference_id: createdNote._id,
      title: createdNote.title,
    });

    // Clear and navigate
    setTitle('');
    setContent('');
    navigate('/meeting_screen');

  } catch (err) {
    console.error('Error during note creation or ticket fetch:', err);
    alert('Failed to create note or send to ticket API.');
  }
};


  return (
    <div style={{ display: 'flex', justifyContent: 'center', marginTop: '40px' }}>
      <div
        style={{
          backgroundColor: '#e1effa',
          padding: '30px',
          borderRadius: '10px',
          width: '400px',
          border: '1px solid #ccc',
          boxShadow: '0 0 5px rgba(0,0,0,0.1)',
        }}
      >
        <h2 style={{ textAlign: 'center' }}>Create Meeting Note</h2>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ fontWeight: 'bold', fontSize: '16px' }}>Title:</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '8px',
                borderRadius: '4px',
                border: '1px solid #ccc',
                marginTop: '5px',
              }}
            />
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ fontWeight: 'bold', fontSize: '16px' }}>Content:</label>
            <textarea
              value={content}
              onChange={(e) => setContent(e.target.value)}
              rows={6}
              required
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: '4px',
                border: '1px solid #ccc',
                marginTop: '5px',
                resize: 'none',
              }}
            />
          </div>

          <button
            type="submit"
            style={{
              backgroundColor: '#4CAF50',
              color: 'white',
              padding: '8px 20px',
              border: 'none',
              borderRadius: '20px',
              cursor: 'pointer',
              fontWeight: 'bold',
            }}
          >
            Save Note
          </button>
        </form>
      </div>
    </div>
  );
};

export default MinuteCreate;
