import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { FiEye, FiEyeOff } from 'react-icons/fi';
import './login.css'; // Import the CSS file for the animated background

export default function Login() {
  const [form, setForm] = useState({ username: '', password: '' });
  const [isFocused, setIsFocused] = useState({
    username: false,
    password: false
  });
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/api/auth/login`, form);
      localStorage.setItem('token', res.data.token);
      alert('Login successful!');
      navigate('/meeting_screen');
    } catch (err) {
      alert(err.response.data.message || 'Login error');
    }
  };

  return (
    <div className="login-container">
      <section className="wave">
        <span></span>
        <span></span>
        <span></span>
      </section>
      <div className="header-text">
        Attendance and Minutes Recording System
      </div>
      <form onSubmit={handleLogin} className="login-form">
        <h2 className="form-title">Login</h2>
        
        <div className="input-group">
          <input 
            type="text" 
            value={form.username}
            onChange={e => setForm({ ...form, username: e.target.value })}
            onFocus={() => setIsFocused({ ...isFocused, username: true })}
            onBlur={() => setIsFocused({ ...isFocused, username: form.username.length > 0 })}
            required
          />
          <label className={isFocused.username || form.username.length > 0 ? 'focused' : ''}>
            Username
          </label>
        </div>
        
        <div className="input-group">
          <input 
            type={showPassword ? "text" : "password"}
            value={form.password}
            onChange={e => setForm({ ...form, password: e.target.value })}
            onFocus={() => setIsFocused({ ...isFocused, password: true })}
            onBlur={() => setIsFocused({ ...isFocused, password: form.password.length > 0 })}
            required
          />
          <label className={isFocused.password || form.password.length > 0 ? 'focused' : ''}>
            Password
          </label>
          <button
            type="button"
            onClick={() => setShowPassword(!showPassword)}
            className="toggle-password"
          >
            {showPassword ? <FiEyeOff size={18} /> : <FiEye size={18} />}
          </button>
        </div>
        
        <button type="submit" className="submit-button">
          Login
        </button>
      </form>
    </div>
  );
}
