import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNoteById } from '../../services/notesService';
import { getAllUsers } from '../../services/userService';

const MinuteNoteDetail = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNoteAndUsers = async () => {
      try {
        const noteResponse = await getNoteById(id);
        const usersResponse = await getAllUsers();

        setNote(noteResponse.data);
        setUsers(usersResponse.data.users);
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchNoteAndUsers();
  }, [id]);

  const handleBack = () => {
    navigate(-1);
  };

  if (!note || !users.length) return <div>Loading...</div>;

  const attendees = users.filter(user =>
    note.attendees?.map(String).includes(String(user.id))
  );

  return (
    <div style={{ padding: '20px', fontFamily: 'Segoe UI, sans-serif' }}>
      <button
        onClick={handleBack}
        style={{
          backgroundColor: '#c9c9c9',
          padding: '6px 12px',
          border: 'none',
          borderRadius: '8px',
          marginBottom: '20px',
          cursor: 'pointer'
        }}
      >
        ← Back
      </button>

      <h1 style={{ fontStyle: 'italic', fontWeight: 'normal' }}>{note.title}</h1>

      <p
        style={{
          backgroundColor: '#e0ecff',
          padding: '20px',
          borderRadius: '10px',
          fontStyle: 'italic',
          minWidth: '400px',
          minHeight: '100px',
          width: 'fit-content',
          maxWidth: '600px',
          marginBottom: '20px'
        }}
      >
        {note.content}
      </p>

      <h3 style={{ marginTop: '20px' }}>Attendees:</h3>
      <ul style={{ listStyleType: 'none', paddingLeft: '0' }}>
        {attendees.length > 0 ? (
          attendees.map((user) => (
            <li key={user.id} style={{ marginBottom: '10px', fontStyle: 'italic' }}>
              ✅ {user.firstName} {user.lastName}
            </li>
          ))
        ) : (
          <li style={{ fontStyle: 'italic' }}>No attendees recorded.</li>
        )}
      </ul>
    </div>
  );
};

export default MinuteNoteDetail;
