// src/pages/attendance/AttendanceDetails.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const AttendanceDetails = () => {
  const { id } = useParams();
  const [record, setRecord] = useState(null);

  useEffect(() => {
    axios.get(`/api/attendance/${id}`)
      .then(res => setRecord(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!record) return <div>Loading...</div>;

  return (
    <div>
      <h2>Attendance for {new Date(record.meeting_date).toLocaleDateString()}</h2>
      <ul>
        {record.attendees.map((att, idx) => (
          <li key={idx}>{att.name} â€“ {att.department}</li>
        ))}
      </ul>
    </div>
  );
};

export default AttendanceDetails;
