// src/pages/attendance/attendance_details.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchAttendance } from '../../services/api';

const AttendanceDetails = () => {
  const { id } = useParams();
  const [record, setRecord] = useState(null);

  useEffect(() => {
    fetchAttendance()
      .then(res => {
        const match = res.data.find(item => item._id === id);
        setRecord(match);
      })
      .catch(err => console.error('Error fetching record:', err));
  }, [id]);

  if (!record) return <p>Loading...</p>;

  return (
    <div>
      <h2>Attendance Details</h2>
      <p><strong>Date:</strong> {new Date(record.meeting_date).toLocaleDateString()}</p>
      <p><strong>Attendees:</strong></p>
      <ul>
        {record.attendees.map((attendee, index) => (
          <li key={index}>{attendee}</li>
        ))}
      </ul>
    </div>
  );
};

export default AttendanceDetails;
