import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getNotes, deleteNoteById } from '../../services/notesService'; // Import deleteNoteById

const MeetingScreen = () => {
  const [meetingNotes, setMeetingNotes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMeetingNotes = async () => {
      try {
        const response = await getNotes(); // Fetch all notes
        const filtered = response.data.filter(note => note.isMinute === true); // Filter for meeting notes
        setMeetingNotes(filtered);
      } catch (err) {
        console.error('Error fetching meeting notes:', err);
      }
    };

    fetchMeetingNotes();
  }, []);

  const handleAddNote = () => {
    navigate('/minute_create'); // Navigate to the create note page
  };

  const handleEditNote = (id) => {
    navigate(`/minute_note_edit/${id}`); // Navigate to edit note page
  };

  const handleDeleteNote = async (id) => {
    try {
      await deleteNoteById(id); // Delete note by ID
      setMeetingNotes(prev => prev.filter(note => note._id !== id)); // Update the state after deletion
    } catch (err) {
      console.error('Failed to delete note:', err);
    }
  };

  const formatDate = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString(); // Format date
  };

  return (
    <div>
      <h1>Meeting Notes</h1>
      <button onClick={handleAddNote}>Add Meeting Note</button> {/* Add button for creating new notes */}
      {meetingNotes.length === 0 ? (
        <p>No meeting notes found.</p>
      ) : (
        <ul>
          {meetingNotes.map(note => (
            <li key={note._id} style={{ marginBottom: '1em' }}>
              <strong>Title:</strong> {note.title}<br />
              <strong>Content:</strong> {note.content.slice(0, 100)}...<br />
              <strong>Created:</strong> {formatDate(note.createdAt)}<br />
              <button onClick={() => handleEditNote(note._id)}>Edit</button>
              <button onClick={() => handleDeleteNote(note._id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MeetingScreen;
