import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AttendanceList = () => {
  const [attendances, setAttendances] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAttendances = async () => {
      try {
        const response = await axios.get('http://localhost:5003/api/attendance/list');
        setAttendances(response.data);
      } catch (error) {
        console.error('Error fetching attendance records:', error);
      }
    };
    fetchAttendances();
  }, []);

  const handleDelete = async () => {
    try {
      await axios.delete('http://localhost:5003/api/attendance/delete-all');
      setAttendances([]);
    } catch (error) {
      console.error('Error deleting attendance records:', error);
    }
  };

  return (
    <div>
      <h2 style={{ marginBottom: '10px' }}>Attendance</h2>

      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px' }}>
        <button 
          onClick={() => navigate('/attendance/create')} 
          style={{
            padding: '8px 16px',
            backgroundColor: '#b4d8f7',
            color: 'black',
            border: 'none',
            borderRadius: '20px',
            cursor: 'pointer',
            fontWeight: '500'
          }}
        >
          Add Attendance
        </button>

        <button
          onClick={handleDelete}
          style={{
            padding: '8px 16px',
            backgroundColor: '#f89b9b',
            color: 'black',
            border: 'none',
            borderRadius: '20px',
            cursor: 'pointer',
            fontWeight: '500'
          }}
        >
          Delete Attendance
        </button>
      </div>

      <ul style={{ paddingLeft: '20px', fontStyle: 'italic' }}>
        {attendances.map((attendance) => (
          <li
            key={attendance._id}
            onClick={() => navigate(`/attendance/details/${attendance._id}`)}
            style={{
              cursor: 'pointer',
              marginBottom: '10px'
            }}
          >
            {new Date(attendance.meeting_date).toLocaleDateString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AttendanceList;
