import axios from 'axios';

const API_BASE_URL = 'http://localhost:5003/api/notes';

export const getNotes = async () => {
  try {
    const response = await axios.get('/api/notes'); // Ensure this returns notes with `isMinute`
    console.log('API response:', response.data); // Debugging: Log the response from the API
    return response.data;
  } catch (err) {
    console.error('Error fetching notes:', err);
    throw err; // Ensure to throw errors to handle them in your components
  }
};
export const createNote = (data) => axios.post(API_BASE_URL, data);
export const updateNote = (id, data) => axios.put(`${API_BASE_URL}/${id}`, data);  // New function for updating notes
export const submitNoteForApproval = (id) => axios.patch(`${API_BASE_URL}/${id}/submit`);  // New function for submitting notes
