// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import NotesListPage from './pages/notes_listpage';
import CreateNotePage from './pages/create';
import { getAttendance } from './services/api';

function App() {
  const [activePage, setActivePage] = useState('notes');
  const [attendanceList, setAttendanceList] = useState([]);

  useEffect(() => {
    if (activePage === 'attendance') {
      getAttendance()
        .then((res) => {
          setAttendanceList(res.data);
          console.log('API connection successful!', res.data);
        })
        .catch((err) => {
          console.error('Error fetching attendance:', err);
        });
    }
  }, [activePage]);

  return (
    <Router>
      <div style={{ display: 'flex' }}>
        <div style={{
          width: '200px',
          padding: '10px',
          borderRight: '1px solid #ccc',
          height: '100vh'
        }}>
          <button onClick={() => setActivePage('notes')}>Notes</button>
          <button onClick={() => setActivePage('googleMeet')}>Google Meet</button>
          <button onClick={() => setActivePage('meeting')}>Meeting</button>
          <button onClick={() => setActivePage('attendance')}>Attendance</button>
        </div>
        <div style={{ flex: 1, padding: '20px' }}>
          <Routes>
            <Route path="/" element={<NotesListPage />} />
            <Route path="/create-note" element={<CreateNotePage />} />
          </Routes>
          {activePage === 'attendance' && (
            <div>
              <h1>Attendance Records</h1>
              <ul>
                {attendanceList.map((entry, index) => (
                  <li key={index}>
                    {entry.name} - {entry.status}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </Router>
  );
}

export default App;
