import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNotes, updateNote } from '../../services/notesService';

const MinuteNoteEdit = () => {
  const { id: noteId } = useParams();
  const navigate = useNavigate();

  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  useEffect(() => {
    const fetchNote = async () => {
      try {
        const response = await getNotes();
        const note = response.data.find(n => n._id === noteId);
        if (note) {
          setTitle(note.title);
          setContent(note.content);
        }
      } catch (err) {
        console.error('Failed to fetch note:', err);
      }
    };

    fetchNote();
  }, [noteId]);

  const handleSubmit = async () => {
    try {
      await axios.put(`/api/notes/${noteId}`, { title, content, isMinute: true });
      console.log('Note updated!');
      navigate('/meeting_screen'); // ✅ go back after update
    } catch (error) {
      console.error('Update failed:', error);
    }
  };

  return (
    <div>
      <h2>Edit Meeting Note</h2>
      <input
        type="text"
        value={title}
        onChange={e => setTitle(e.target.value)}
        placeholder="Note Title"
      />
      <br />
      <textarea
        value={content}
        onChange={e => setContent(e.target.value)}
        placeholder="Note Content"
        rows={10}
        cols={50}
      />
      <br />
      <button onClick={handleSave}>Save</button>
    </div>
  );
};

export default MinuteNoteEdit;
