import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNotes } from '../services/notesService';

const MinuteNoteDetail = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNote = async () => {
      try {
        const response = await getNotes();
        const foundNote = response.data.find(note => note._id === id);
        setNote(foundNote);
      } catch (err) {
        console.error('Error fetching note:', err);
      }
    };
    
    fetchNote();
  }, [id]);

  const handleEdit = () => {
    navigate(`/edit_note/${id}`); // Navigate to an edit page (you can create this)
  };

  const handleSubmitForApproval = () => {
    console.log('Sending for approval...');
    // Handle the "Send for Approval" logic here (e.g., update status in DB)
  };

  if (!note) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{note.title}</h1>
      <p>{note.content}</p>
      <button onClick={handleSubmitForApproval}>Send for Approval</button>
      <button onClick={handleEdit}>Edit</button>
    </div>
  );
};

export default MinuteNoteDetail;
