import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNotes, updateNote } from '../services/notesService';

const MinuteNoteEdit = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);
  const [content, setContent] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNote = async () => {
      try {
        const response = await getNotes();
        const foundNote = response.data.find(note => note._id === id);
        setNote(foundNote);  // Set the found note to state
        setContent(foundNote.content);  // Set content to be editable
      } catch (err) {
        console.error('Error fetching note:', err);
      }
    };
    
    fetchNote();
  }, [id]);

  const handleSave = async () => {
    try {
      const updatedNote = { ...note, content };
      await updateNote(id, updatedNote);  // Update the note via API
      navigate(`/minute_note/${id}`);  // Navigate back to the note detail
    } catch (err) {
      console.error('Error updating note:', err);
    }
  };

  if (!note) {
    return <div>Loading...</div>;  // Show loading if note is still being fetched
  }

  return (
    <div>
      <h1>Edit Note: {note.title}</h1>
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}  // Update content in state
      />
      <button onClick={handleSave}>Save Changes</button>
    </div>
  );
};

export default MinuteNoteEdit;
