import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function MeetingScreen() {
  const [minuteNotes, setMinuteNotes] = useState([]);

  // Fetch minute notes on page load
  useEffect(() => {
    fetchMinuteNotes();
  }, []); // Empty dependency array means it runs once when the component is mounted

  const fetchMinuteNotes = async () => {
    try {
      const response = await axios.get('/api/notes?isMinute=true');
      setMinuteNotes(response.data);
    } catch (error) {
      console.error('Failed to fetch minute notes', error);
    }
  };

  return (
    <div>
      <h1>Meeting Notes</h1>
      <ul>
        {minuteNotes.map((note) => (
          <li key={note._id}>
            <Link to={`/minute_note/${note._id}`}>{note.title}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MeetingScreen;
