// src/pages/attendance/attendance_form.jsx
import React, { useState } from 'react';
import { postAttendance } from '../../services/api';
import { useNavigate } from 'react-router-dom';

const AttendanceForm = () => {
  const [formData, setFormData] = useState({
    meeting_date: '',
    attendees: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await postAttendance({
        ...formData,
        attendees: formData.attendees.split(',').map(name => name.trim())
      });
      navigate('/attendance');
    } catch (err) {
      console.error('Failed to submit attendance:', err);
    }
  };

  return (
    <div>
      <h2>Add Attendance</h2>
      <form onSubmit={handleSubmit}>
        <label>Date:</label><br />
        <input type="date" name="meeting_date" value={formData.meeting_date} onChange={handleChange} required /><br /><br />

        <label>Attendees (comma-separated):</label><br />
        <textarea name="attendees" value={formData.attendees} onChange={handleChange} required /><br /><br />

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AttendanceForm;
