// src/services/notesService.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5003/api/notes';

// Create a new note
export const createNote = async (data) => {
  try {
    const response = await axios.post(API_BASE_URL, data);
    return response.data; // Return the saved note or success message
  } catch (err) {
    console.error('Error creating note:', err);
    throw err; // Throw the error so it can be handled in the component
  }
};

// Get all notes
export const getNotes = () => axios.get(API_BASE_URL);

// Get a single note by ID
export const getNoteById = (id) => axios.get(`${API_BASE_URL}/${id}`);

// Update an existing note
export const updateNote = (id, data) => axios.put(`${API_BASE_URL}/${id}`, data);

// Submit a note for approval (example of patch operation)
export const submitNoteForApproval = (id) => axios.patch(`${API_BASE_URL}/${id}/submit`);

// Delete a note
export const deleteNote = (id) => axios.delete(`${API_BASE_URL}/${id}`);
