// src/pages/attendance/attendance_form.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { postAttendance } from '../../services/api';

const AttendanceForm = () => {
  const [meetingDate, setMeetingDate] = useState('');
  const [attendees, setAttendees] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate form data (ensure non-empty)
    if (!meetingDate || !attendees) {
      alert("Please fill in all fields!");
      return;
    }

    // Convert attendees to an array
    const attendeesArray = attendees.split(',').map((attendee) => attendee.trim());

    const newAttendance = {
      meeting_date: meetingDate,
      attendees: attendeesArray
    };

    try {
        console.log("gray");
      await postAttendance(newAttendance);
      
      console.log('Attendance added successfully');
      navigate('/attendance'); // Navigate back to the list page after successful submission
    } catch (err) {
      console.error('Failed to submit attendance:', err);
      alert('Error submitting attendance. Please try again later.');
    }
  };

  return (
    <div>
      <h2>Add Attendance</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Meeting Date:</label>
          <input
            type="date"
            value={meetingDate}
            onChange={(e) => setMeetingDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Attendees (comma-separated):</label>
          <input
            type="text"
            value={attendees}
            onChange={(e) => setAttendees(e.target.value)}
            required
          />
        </div>
        <button type="submit">Submit Attendance</button>
      </form>
    </div>
  );
};

export default AttendanceForm;
