import React from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import NotesPage from './pages/notes_listpage';
import CreateNotePage from './pages/create_notes';
import MinuteNoteDetail from './pages/minute_note_detail'; // Import the minute_note_detail page
import GoogleMeetButton from './components/GoogleMeetButton';
import { getAttendance } from './services/api';
import MeetingScreen from './pages/meeting_screen'; 

function Sidebar({ setActivePage }) {
  return (
    <div style={{
      width: '200px',
      padding: '10px',
      borderRight: '1px solid #ccc',
      height: '100vh'
    }}>
      <button onClick={() => setActivePage('notes')}>Notes</button>
      <button onClick={() => setActivePage('googleMeet')}>Google Meet</button>
      <button onClick={() => setActivePage('meeting')}>Meeting</button>
      <button onClick={() => setActivePage('attendance')}>Attendance</button>
    </div>
  );
}

function AppContent({ activePage }) {
  const [attendanceList, setAttendanceList] = React.useState([]);

  React.useEffect(() => {
    if (activePage === 'attendance') {
      getAttendance()
        .then((res) => {
          setAttendanceList(res.data);
          console.log('API connection successful!', res.data);
        })
        .catch((err) => {
          console.error('Error fetching attendance:', err);
        });
    }
  }, [activePage]);

  switch (activePage) {
    case 'notes':
      return <NotesPage />;
    case 'googleMeet':
      return (
        <div>
          <h2>Google Meet</h2>
          <GoogleMeetButton />
        </div>
      );
    case 'meeting':
      return <MeetingScreen />; // ðŸ‘ˆ Use your real component here
    case 'attendance':
      return (
        <div>
          <h1>Attendance Records</h1>
          <ul>
            {attendanceList.map((entry, index) => (
              <li key={index}>
                {entry.name} - {entry.status}
              </li>
            ))}
          </ul>
        </div>
      );
    default:
      return <NotesPage />;
  }
}

function App() {
  const [activePage, setActivePage] = React.useState('notes');

  return (
    <div style={{ display: 'flex' }}>
      <Sidebar setActivePage={setActivePage} />
      <div style={{ flex: 1, padding: '20px' }}>
        <Routes>
          <Route path="/" element={<AppContent activePage={activePage} />} />
          <Route path="/create_notes" element={<CreateNotePage />} />
          <Route path="/note/:id" element={<MinuteNoteDetail />} /> 
          <Route path="/meeting_screen" element={<></>}/>
        </Routes>
      </div>
    </div>
  );
}

export default App;
