import MeetingScreen from './pages/meeting_screen'; // ðŸ‘ˆ Add this at the top

// ...

function AppContent({ activePage }) {
  const [attendanceList, setAttendanceList] = React.useState([]);

  React.useEffect(() => {
    if (activePage === 'attendance') {
      getAttendance()
        .then((res) => {
          setAttendanceList(res.data);
          console.log('API connection successful!', res.data);
        })
        .catch((err) => {
          console.error('Error fetching attendance:', err);
        });
    }
  }, [activePage]);

  switch (activePage) {
    case 'notes':
      return <NotesPage />;
    case 'googleMeet':
      return (
        <div>
          <h2>Google Meet</h2>
          <GoogleMeetButton />
        </div>
      );
    case 'meeting':
      return <MeetingScreen />; // ðŸ‘ˆ Use your real component here
    case 'attendance':
      return (
        <div>
          <h1>Attendance Records</h1>
          <ul>
            {attendanceList.map((entry, index) => (
              <li key={index}>
                {entry.name} - {entry.status}
              </li>
            ))}
          </ul>
        </div>
      );
    default:
      return <NotesPage />;
  }
}
