import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import background from '../../assets/background.png';
import { FiEye, FiEyeOff } from 'react-icons/fi';

export default function Login() {
  const [form, setForm] = useState({ username: '', password: '' });
  const [isFocused, setIsFocused] = useState({
    username: false,
    password: false
  });
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/api/auth/login`, form);
      localStorage.setItem('token', res.data.token);
      alert('Login successful!');
      navigate('/meeting_screen');
    } catch (err) {
      alert(err.response.data.message || 'Login error');
    }
  };

  return (
    <div style={{
      position: 'relative',
      width: '100vw',
      height: '100vh',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center'
    }}>
      {/* Animated Background */}
      <div style={{
        position: 'absolute',
        left: 0,
        top: 0,
        width: '100%',
        height: '100%',
        background: '#4973ff',
        boxShadow: 'inset 0 0 50px rgba(0, 0, 0, 0.5)',
        zIndex: 0
      }}>
        <span style={{
          content: '""',
          position: 'absolute',
          width: '325vh',
          height: '325vh',
          top: 0,
          left: '50%',
          transform: 'translate(-50%, -75%)',
          borderRadius: '45%',
          background: 'rgba(20, 20, 20, 1)',
          animation: 'animate1 5s linear infinite'
        }}></span>
        <span style={{
          content: '""',
          position: 'absolute',
          width: '325vh',
          height: '325vh',
          top: 0,
          left: '50%',
          transform: 'translate(-50%, -75%)',
          borderRadius: '40%',
          background: 'rgba(20, 20, 20, 0.5)',
          animation: 'animate2 10s linear infinite'
        }}></span>
        <span style={{
          content: '""',
          position: 'absolute',
          width: '325vh',
          height: '325vh',
          top: 0,
          left: '50%',
          transform: 'translate(-50%, -75%)',
          borderRadius: '42.5%',
          background: 'rgba(20, 20, 20, 0.5)',
          animation: 'animate3 15s linear infinite'
        }}></span>
      </div>

      <div style={{
        width: '100%',
        padding: '1.5rem 0',
        textAlign: 'center',
        zIndex: 1
      }}>
        <h1 style={{
          color: 'white',
          fontSize: '1.2rem',
          fontWeight: '500',
          margin: 0,
          textShadow: '0 1px 3px rgba(0, 0, 0, 0.5)'
        }}>
          Attendance and Minutes Recording System
        </h1>
      </div>

      <div style={{
        flex: 1,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        width: '100%'
      }}>
        <form onSubmit={handleLogin} style={{
          position: 'relative',
          width: '90%',
          maxWidth: '400px',
          padding: '2.5rem',
          borderRadius: '12px',
          backgroundColor: 'rgba(51, 65, 85, 0.5)',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          zIndex: 1
        }}>
          <h2 style={{
            color: '#f8fafc',
            textAlign: 'center',
            marginBottom: '2.5rem',
            fontSize: '1.75rem',
            fontWeight: '600'
          }}>Login</h2>

          <div style={{
            position: 'relative',
            marginBottom: '2rem'
          }}>
            <div style={{
              position: 'absolute',
              bottom: 0,
              left: 0,
              right: 0,
              height: '2px',
              backgroundColor: 'rgba(255, 255, 255, 0.2)',
              zIndex: 1
            }} />
            <div style={{
              position: 'absolute',
              bottom: 0,
              left: 0,
              right: 0,
              height: '2px',
              backgroundColor: '#3b82f6',
              zIndex: 2,
              transform: isFocused.username ? 'scaleX(1)' : 'scaleX(0)',
              transformOrigin: 'center',
              transition: 'transform 0.3s ease'
            }} />
            <input
              type="text"
              value={form.username}
              onChange={e => setForm({ ...form, username: e.target.value })}
              onFocus={() => setIsFocused({ ...isFocused, username: true })}
              onBlur={() => setIsFocused({ ...isFocused, username: form.username.length > 0 })}
              required
              style={{
                width: '100%',
                padding: '0.5rem 0',
                backgroundColor: 'transparent',
                border: 'none',
                borderBottom: 'none',
                color: '#f8fafc',
                fontSize: '1rem',
                outline: 'none',
                transition: 'all 0.3s ease'
              }}
            />
            <label style={{
              position: 'absolute',
              left: 0,
              top: isFocused.username || form.username.length > 0 ? '-1.25rem' : '0.5rem',
              color: isFocused.username ? '#3b82f6' : 'rgba(248, 250, 252, 0.7)',
              fontSize: isFocused.username || form.username.length > 0 ? '0.75rem' : '1rem',
              transition: 'all 0.3s ease',
              pointerEvents: 'none'
            }}>
              Username
            </label>
          </div>

          <div style={{
            position: 'relative',
            marginBottom: '2.5rem'
          }}>
            <div style={{
              position: 'absolute',
              bottom: 0,
              left: 0,
              right: 0,
              height: '2px',
              backgroundColor: 'rgba(255, 255, 255, 0.2)',
              zIndex: 1
            }} />
            <div style={{
              position: 'absolute',
              bottom: 0,
              left: 0,
              right: 0,
              height: '2px',
              backgroundColor: '#3b82f6',
              zIndex: 2,
              transform: isFocused.password ? 'scaleX(1)' : 'scaleX(0)',
              transformOrigin: 'center',
              transition: 'transform 0.3s ease'
            }} />
            <input
              type={showPassword ? "text" : "password"}
              value={form.password}
              onChange={e => setForm({ ...form, password: e.target.value })}
              onFocus={() => setIsFocused({ ...isFocused, password: true })}
              onBlur={() => setIsFocused({ ...isFocused, password: form.password.length > 0 })}
              required
              style={{
                width: '100%',
                padding: '0.5rem 0',
                backgroundColor: 'transparent',
                border: 'none',
                borderBottom: 'none',
                color: '#f8fafc',
                fontSize: '1rem',
                outline: 'none',
                transition: 'all 0.3s ease',
                paddingRight: '2rem'
              }}
            />
            <label style={{
              position: 'absolute',
              left: 0,
              top: isFocused.password || form.password.length > 0 ? '-1.25rem' : '0.5rem',
              color: isFocused.password ? '#3b82f6' : 'rgba(248, 250, 252, 0.7)',
              fontSize: isFocused.password || form.password.length > 0 ? '0.75rem' : '1rem',
              transition: 'all 0.3s ease',
              pointerEvents: 'none'
            }}>
              Password
            </label>
            <button
              type="button"
              onClick={() => setShowPassword(!showPassword)}
              style={{
                position: 'absolute',
                right: '0',
                top: '50%',
                transform: 'translateY(-50%)',
                background: 'transparent',
                border: 'none',
                color: 'rgba(248, 250, 252, 0.7)',
                cursor: 'pointer',
                padding: '0.5rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                ':hover': {
                  color: '#3b82f6'
                }
              }}
            >
              {showPassword ? <FiEyeOff size={18} /> : <FiEye size={18} />}
            </button>
          </div>

          <button
            type="submit"
            style={{
              width: '100%',
              padding: '0.875rem',
              borderRadius: '8px',
              border: 'none',
              backgroundColor: '#3b82f6',
              color: 'white',
              fontSize: '1rem',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              marginTop: '1rem',
              ':hover': {
                backgroundColor: '#2563eb',
                transform: 'translateY(-2px)',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)'
              },
              ':active': {
                transform: 'translateY(0)'
              }
            }}
          >
            Login
          </button>
        </form>
      </div>
      <style>
        {`
    @keyframes animate1 {
      0% {
        transform: translate(-50%, -75%) rotate(0deg);
      }
      100% {
        transform: translate(-50%, -75%) rotate(360deg);
      }
    }
    @keyframes animate2 {
      0% {
        transform: translate(-50%, -75%) rotate(0deg);
      }
      100% {
        transform: translate(-50%, -75%) rotate(360deg);
      }
    }
    @keyframes animate3 {
      0% {
        transform: translate(-50%, -75%) rotate(0deg);
      }
      100% {
        transform: translate(-50%, -75%) rotate(360deg);
      }
    }
  `}
      </style>
    </div>
  );
}
