import React, { useEffect, useState } from 'react';
import { getNotes, deleteNote } from '../../services/notesService'; // ðŸ‘ˆ Make sure deleteNote is defined
import { useNavigate } from 'react-router-dom';

const MeetingScreen = () => {
  const [meetingNotes, setMeetingNotes] = useState([]);
  const navigate = useNavigate();

  const fetchMeetingNotes = async () => {
    try {
      const response = await getNotes();
      const meetingNotes = response.data.filter(note => note.isMinute === true);
      setMeetingNotes(meetingNotes);
    } catch (err) {
      console.error('Error fetching notes:', err);
    }
  };

  useEffect(() => {
    fetchMeetingNotes();
  }, []);

  const handleNoteClick = (noteId) => {
    navigate(`/minute_note/${noteId}`);
  };

  const handleCreateClick = () => {
    navigate('/minute_create');
  };

  const handleDelete = async (noteId) => {
    try {
      await deleteNote(noteId); // ðŸ‘ˆ Call delete API
      // Refresh the list after deletion
      setMeetingNotes(prevNotes => prevNotes.filter(note => note._id !== noteId));
    } catch (err) {
      console.error('Failed to delete note:', err);
    }
  };

  const formatDate = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString();
  };

  return (
    <div>
      <h1>Meeting Notes</h1>
      <button onClick={handleCreateClick}>Create Meeting Note</button>

      {meetingNotes.length === 0 ? (
        <p>No meeting notes found.</p>
      ) : (
        <ul>
          {meetingNotes.map(note => (
            <li
              key={note._id}
              style={{
                marginBottom: '1em',
                padding: '10px',
                border: '1px solid #ccc',
                borderRadius: '8px',
              }}
            >
              <div onClick={() => handleNoteClick(note._id)} style={{ cursor: 'pointer' }}>
                <strong>Title:</strong> {note.title}<br />
                <strong>Content:</strong> {note.content.slice(0, 100)}...<br />
                <strong>Created:</strong> {formatDate(note.createdAt)}
              </div>
              <button onClick={() => handleDelete(note._id)} style={{ marginTop: '5px' }}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MeetingScreen;
