import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { getNotes, getTicketStatus } from '../../services/notesService';

const MeetingScreen = () => {
  const [meetingNotes, setMeetingNotes] = useState([]);
  const [filter, setFilter] = useState(['Approved', 'For Approval', 'Declined']); // Track active filters
  const [isAccordionOpen, setIsAccordionOpen] = useState(false); // Track accordion state
  const [searchQuery, setSearchQuery] = useState(''); // Track search input
  const navigate = useNavigate();
  const location = useLocation();

  // Fetch meeting notes along with ticket statuses
  const fetchMeetingNotes = async () => {
    try {
      const response = await getNotes(); // Fetch notes
      const allNotes = response.data;

      // Fetch ticket statuses for all notes
      const notesWithStatus = await Promise.all(
        allNotes.map(async (note) => {
          try {
            const res = await getTicketStatus(note._id); // Fetch status for each note
            const ticketStatus = res.data.ticket[0]?.status || 'Unavailable'; // Using optional chaining in case there's no status
            return { ...note, ticketStatus }; // Save the status to note
          } catch (err) {
            return { ...note, ticketStatus: 'Unavailable' }; // If there's an error, mark as 'Unavailable'
          }
        })
      );

      setMeetingNotes(notesWithStatus); // Combine all notes into a single list
    } catch (err) {
      console.error('Error fetching notes:', err);
    }
  };

  useEffect(() => {
    fetchMeetingNotes(); // Fetch notes whenever the location changes
  }, [location]);

  const handleNoteClick = (noteId) => {
    navigate(`/minute_note_detail/${noteId}`); // Navigate to note detail page
  };

  const formatDate = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const toggleFilter = (status) => {
    setFilter((prevFilter) =>
      prevFilter.includes(status)
        ? prevFilter.filter((item) => item !== status) // Remove status if active
        : [...prevFilter, status] // Add status if inactive
    );
  };

  const filteredNotes = meetingNotes.filter(
    (note) =>
      filter.includes(note.ticketStatus) &&
      note.title.toLowerCase().startsWith(searchQuery.toLowerCase()) // Filter by starting letter
  );

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      backgroundColor: '#141414',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      overflowY: 'auto',
      padding: '2rem',
      boxSizing: 'border-box'
    }}>
      <br></br>
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: 0
      }} />

      <div style={{
        position: 'relative',
        maxWidth: '1200px',
        margin: '0 auto',
        zIndex: 1
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '2rem',
          flexWrap: 'wrap',
          gap: '1rem'
        }}>
          <h1 className="wave-text" style={{
            margin: 0,
            fontWeight: '600',
            textShadow: '0 2px 4px rgba(0,0,0,0.3)',
            display: 'flex',
            gap: '0.2rem',
            color: 'white',
          }}>
            {'Meeting Minutes'.split('').map((char, index) => (
              <span key={index} style={{ animationDelay: `${index * 0.2}s` }}>
                {char === ' ' ? '\u00A0' : char} {}
              </span>
            ))} 
          </h1>

          <button
            onClick={() => navigate('/login')}
            className="logout-btn"
          >
            <div className="sign">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                <path d="M10 9v-2h10v10h-10v-2h-2v4h14v-14h-14v4h2zm-6 3l4-4v3h8v2h-8v3l-4-4z" />
              </svg>
            </div>
            <span className="text">Logout</span>
          </button>
        </div>


        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '1rem',
          flexWrap: 'wrap',
          gap: '1rem'
        }}>
          <div style={{ display: 'flex', gap: '1rem' }}>
            <input
              type="text"
              placeholder="Search by title..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              style={{
                flex: '0 0 300px', 
                padding: '0.5rem 1rem',
                borderRadius: '8px',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                backgroundColor: 'rgba(51, 65, 85, 0.7)',
                color: 'white',
                fontSize: '1rem',
                outline: 'none',
                transition: 'all 0.3s ease',
              }}
            />

            <button
              onClick={() => navigate('/minute_create')}
              className="button"
            >
              <span>Create</span>
            </button>

            <button
              onClick={fetchMeetingNotes} // Call the fetchMeetingNotes function to refresh data
              className="refresh-btn"
            >
              <span>Refresh</span>
            </button>
          </div>

          <button
            onClick={() => setIsAccordionOpen(!isAccordionOpen)}
            style={{
              backgroundColor: '#4973ff',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '0.5rem 1rem',
              fontSize: '1rem',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
            }}
          >
            Status
          </button>
        </div>

        <div
          style={{
            maxHeight: isAccordionOpen ? '200px' : '0',
            opacity: isAccordionOpen ? 1 : 0,
            transform: isAccordionOpen ? 'translateY(0)' : 'translateY(-10px)',
            overflow: 'hidden',
            padding: isAccordionOpen ? '1rem 0' : '0',
            transition: 'max-height 0.5s ease, opacity 0.5s ease, transform 0.5s ease, padding 0.5s ease',
          }}
        >
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button
              onClick={() => toggleFilter('Approved')}
              style={{
                backgroundColor: filter.includes('Approved') ? '#4973ff' : '#334155b3',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '0.5rem 1rem',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
              }}
            >
              Approved
            </button>
            <button
              onClick={() => toggleFilter('For Approval')}
              style={{
                backgroundColor: filter.includes('For Approval') ? '#4973ff' : '#334155b3',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '0.5rem 1rem',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
              }}
            >
              For Approval
            </button>
            <button
              onClick={() => toggleFilter('Declined')}
              style={{
                backgroundColor: filter.includes('Declined') ? '#4973ff' : '#334155b3',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '0.5rem 1rem',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
              }}
            >
              Declined
            </button>
          </div>
        </div>

        <br></br>
        {filteredNotes.length === 0 ? (
          <div style={{
            backgroundColor: 'rgba(51, 65, 85, 0.5)',
            borderRadius: '12px',
            padding: '2rem',
            textAlign: 'center',
            color: 'rgba(248, 250, 252, 0.7)',
            border: '1px dashed rgba(255,255,255,0.1)'
          }}>
            <p style={{ margin: 0, fontSize: '1.1rem' }}>
              No meeting minutes found
            </p>
          </div>
        ) : (
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
            gap: '1.5rem',
            marginTop: '1rem'
          }}>
            {filteredNotes.map(note => (
              <div
                key={note._id}
                onClick={() => handleNoteClick(note._id)}
                className="note-card"
              >
                <h3 style={{
                  color: '#f8fafc',
                  marginTop: 0,
                  marginBottom: '1rem',
                  fontSize: '1.25rem',
                  fontWeight: '600',
                  whiteSpace: 'nowrap',
                  overflow: 'hidden',
                  textOverflow: 'ellipsis'
                }}>
                  {note.title}
                </h3>
                <div style={{
                  display: 'inline-block', // Ensures the background wraps tightly around the text
                  backgroundColor: note.ticketStatus === 'For Approval' ? 'rgba(255, 223, 0, 0.2)' :
                    note.ticketStatus === 'Declined' ? 'rgba(255, 0, 0, 0.2)' :
                      'rgba(59, 130, 246, 0.2)',
                  color: note.ticketStatus === 'For Approval' ? '#FFD700' :
                    note.ticketStatus === 'Declined' ? '#FF0000' :
                      '#93c5fd',
                  padding: '0.25rem 0.5rem',
                  borderRadius: '4px',
                  fontSize: '0.75rem',
                  marginBottom: '0.5rem'
                }}>
                  {note.ticketStatus}
                </div>
                <div style={{
                  color: 'rgba(248, 250, 252, 0.6)',
                  fontSize: '0.85rem'
                }}>
                  {formatDate(note.createdAt)}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default MeetingScreen;
