import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AttendanceDetails = () => {
  const { id } = useParams();  // Using useParams to get ID from URL
  const [attendance, setAttendance] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        // Fetching the attendance details using the ID from the URL
        const response = await axios.get(`http://localhost:5003/api/attendance/details/${id}`);
        setAttendance(response.data);  // Setting the data in the state
      } catch (error) {
        console.error('Failed to fetch attendance details:', error);
        setError(error);
      }
    };

    if (id) {
      fetchAttendance();  // Fetch the attendance details if the ID is available
    }
  }, [id]);  // Re-run the effect if the ID changes

  // Display error message if an error occurs
  if (error) return <div>Error: {JSON.stringify(error)}</div>;

  // Render the attendance details if data is available
  if (attendance) {
    return (
      <div style={{ padding: '20px', fontFamily: 'Segoe UI, sans-serif' }}>
  <h1 style={{ fontStyle: 'italic', fontWeight: 'normal' }}>Attendance Details</h1>

  <p style={{
    backgroundColor: '#e0ecff',
    padding: '20px',
    borderRadius: '10px',
    fontStyle: 'italic',
    minWidth: '400px',
    minHeight: '100px',
    width: 'fit-content',
    maxWidth: '600px',
    marginBottom: '20px'
  }}>
    <strong>ID:</strong> {attendance._id}
  </p>

  <p style={{
    backgroundColor: '#e0ecff',
    padding: '20px',
    borderRadius: '10px',
    fontStyle: 'italic',
    minWidth: '400px',
    minHeight: '100px',
    width: 'fit-content',
    maxWidth: '600px',
    marginBottom: '20px'
  }}>
    <strong>Date:</strong> {new Date(attendance.meeting_date).toLocaleDateString()}
  </p>

  <p style={{
    backgroundColor: '#e0ecff',
    padding: '20px',
    borderRadius: '10px',
    fontStyle: 'italic',
    minWidth: '400px',
    minHeight: '100px',
    width: 'fit-content',
    maxWidth: '600px',
    marginBottom: '20px'
  }}>
    <strong>Attendees:</strong>
  </p>

  <ul style={{ listStyleType: 'disc', paddingLeft: '20px', fontStyle: 'italic' }}>
    {attendance.attendees.length === 0 ? (
      <li>No attendees yet.</li>
    ) : (
      attendance.attendees.map((attendee, index) => (
        <li key={index}>{attendee.name}</li> // Display the names of attendees
      ))
    )}
  </ul>

  <div style={{ marginTop: '20px', display: 'flex', gap: '10px' }}>
    <button
      onClick={handleEdit}
      style={{
        backgroundColor: '#d9d9d9',
        border: 'none',
        borderRadius: '10px',
        padding: '6px 12px',
        cursor: 'pointer'
      }}
    >
      Edit
    </button>

    <button
      onClick={handleSubmitForApproval}
      style={{
        backgroundColor: '#b4d8f7',
        border: 'none',
        borderRadius: '10px',
        padding: '6px 12px',
        cursor: 'pointer'
      }}
    >
      Send for Approval
    </button>

    <button
      onClick={handleSaveAttendees}
      style={{
        backgroundColor: '#63d374',
        border: 'none',
        borderRadius: '10px',
        padding: '6px 12px',
        cursor: 'pointer'
      }}
    >
      Save Changes
    </button>
  </div>
</div>

    );
  }

  return <div>Loading...</div>;
};

export default AttendanceDetails;
