import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getNoteById, updateNote, deleteNote } from '../../services/notesService';
import { getAllUsers } from '../../services/userService';
import background from '../../assets/background.png';

const MinuteNoteDetail = () => {
  const { id } = useParams();
  const [note, setNote] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedAttendees, setSelectedAttendees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNoteAndUsers = async () => {
      try {
        const noteResponse = await getNoteById(id);
        const usersResponse = await getAllUsers();

        const sortedUsers = usersResponse.data.users.sort((a, b) => 
          a.firstName.localeCompare(b.firstName)
        );

        setNote(noteResponse.data);
        setEditedContent(noteResponse.data.content);
        setUsers(sortedUsers);

        if (noteResponse.data.attendees) {
          const attendeeIds = noteResponse.data.attendees.map(String);
          setSelectedAttendees(attendeeIds);
        }
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchNoteAndUsers();
  }, [id]);

  const handleSave = async () => {
    try {
      await updateNote(id, { 
        content: editedContent,
        attendees: selectedAttendees 
      });
      const updatedNote = await getNoteById(id);
      setNote(updatedNote.data);
      setIsEditing(false);
    } catch (err) {
      console.error('Error saving note:', err);
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete this note?")) {
      try {
        await deleteNote(id);
        navigate('/meeting_screen');
      } catch (err) {
        console.error('Error deleting note:', err);
      }
    }
  };

  const handleAttendeeChange = (userId) => {
    if (!isEditing) return; 
    const idStr = String(userId);
    setSelectedAttendees((prev) =>
      prev.includes(idStr)
        ? prev.filter((id) => id !== idStr)
        : [...prev, idStr]
    );
  };

  const handleMarkAll = () => {
    if (!isEditing) return; 
    if (selectedAttendees.length === users.length) {
      setSelectedAttendees([]);
    } else {
      setSelectedAttendees(users.map((user) => String(user.id)));
    }
  };

  const handleBack = () => {
    navigate('/meeting_screen');
  };

  if (!note || !users.length)
    return (
      <div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100vw',
          height: '100vh',
          backgroundImage: `url(${background})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          color: '#f8fafc',
          fontSize: '1.5rem',
          fontWeight: '600',
        }}
      >
        Loading...
      </div>
    );

  return (
    <div
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        backgroundImage: `url(${background})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        overflowY: 'auto',
        padding: '2rem',
        boxSizing: 'border-box',
      }}
    >
      <div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backdropFilter: 'blur(8px)',
          WebkitBackdropFilter: 'blur(8px)',
          zIndex: 0,
        }}
      />
      <div
        style={{
          position: 'relative',
          maxWidth: '1200px',
          margin: '0 auto',
          zIndex: 1,
          backgroundColor: 'rgba(20, 20, 20, 0.8)',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        }}
      >
        <button
          onClick={handleBack}
          style={{
            backgroundColor: 'rgba(51, 65, 85, 0.7)',
            color: 'rgba(248, 250, 252, 0.9)',
            border: '1px solid rgba(255,255,255,0.2)',
            borderRadius: '8px',
            padding: '0.5rem 1rem',
            fontSize: '1rem',
            fontWeight: '600',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            marginBottom: '1.5rem',
            display: 'inline-flex',
            alignItems: 'center',
            gap: '0.5rem',
          }}
        >
          ← Back to Meeting Notes
        </button>

        <div
          style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '2rem',
            flexWrap: 'wrap',
            gap: '1rem',
          }}
        >
          <h1
            style={{
              color: '#f8fafc',
              fontSize: '1.8rem',
              fontWeight: '600',
              margin: 0,
              textShadow: '0 2px 4px rgba(0,0,0,0.3)',
            }}
          >
            {note.title}
          </h1>
        </div>

        <div
          style={{
            backgroundColor: 'rgba(51, 65, 85, 0.5)',
            borderRadius: '12px',
            padding: '2rem',
            color: 'rgba(248, 250, 252, 0.9)',
            lineHeight: '1.6',
            whiteSpace: 'pre-wrap',
            overflowY: 'auto',
            marginBottom: '2rem',
          }}
        >
          {isEditing ? (
            <textarea
              value={editedContent}
              onChange={(e) => setEditedContent(e.target.value)}
              style={{
                width: '100%',
                height: '200px',
                padding: '1rem',
                borderRadius: '8px',
                border: 'none',
                backgroundColor: 'rgba(51, 65, 85, 0.7)',
                color: '#f8fafc',
                fontSize: '1rem',
                outline: 'none',
                resize: 'none',
              }}
            />
          ) : (
            note.content
          )}
        </div>

        <div
          style={{
            backgroundColor: 'rgba(51, 65, 85, 0.5)',
            borderRadius: '12px',
            padding: '2rem',
            color: 'rgba(248, 250, 252, 0.9)',
            lineHeight: '1.6',
            overflowY: 'auto',
          }}
        >
          <h2
            style={{
              color: '#f8fafc',
              fontSize: '1.25rem',
              fontWeight: '600',
              marginBottom: '1rem',
            }}
          >
            Attendees
          </h2>
          <ul
            style={{
              listStyleType: 'none',
              padding: 0,
              margin: 0,
              display: 'grid',
              gap: '0.5rem',
            }}
          >
            {users.map((user) => (
              <li
                key={user.id}
                style={{
                  padding: '0.75rem',
                  borderRadius: '8px',
                  backgroundColor: selectedAttendees.includes(String(user.id))
                    ? 'rgba(59, 130, 246, 0.2)'
                    : 'rgba(255,255,255,0.05)',
                  color: selectedAttendees.includes(String(user.id))
                    ? '#f8fafc'
                    : 'rgba(248, 250, 252, 0.7)',
                  fontWeight: selectedAttendees.includes(String(user.id))
                    ? '500'
                    : 'normal',
                }}
              >
                {user.firstName} {user.lastName}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default MinuteNoteDetail;
